using System.Collections; using System.Collections.Generic; using UnityEngine;  public class StatTools : MonoBehaviour {      public int FPS = 60;      private float _updateRateTime = 1f;      private float lastUpdateShowTime = 0f;     private int frameCount;     private float finalFPS;                                                                                                                                                                                                                                                                                                                        //----------------      private void Awake()     {         //Application.targetFrameRate = FPS;     }      // Use this for initialization     void Start () {         lastUpdateShowTime = Time.realtimeSinceStartup;         frameCount = 0;     }      // Update is called once per frame     void Update () {         frameCount++;         if (Time.realtimeSinceStartup - lastUpdateShowTime >= _updateRateTime)         {             finalFPS = frameCount / (Time.realtimeSinceStartup - lastUpdateShowTime);             lastUpdateShowTime = Time.realtimeSinceStartup;             frameCount = 0;         }     }      void OnGUI()     {         ShowFPS();     }      private void ShowFPS()     {         GUIStyle fontStyle = new GUIStyle();         fontStyle.normal.background = null;         fontStyle.normal.textColor = new Color(1, 0, 0);         fontStyle.alignment = TextAnchor.UpperRight;         fontStyle.fontSize = 20;         GUI.Label(new Rect(Screen.width - 100, 0, 100, 100), "FPS: " + string.Format("{0:f2}", finalFPS), fontStyle);     }  } 